import React, { Component, Fragment } from "react";
import "./index.scss";
import PracticeItem from "./component/practiceItem";

import { CSSTransition, TransitionGroup } from "react-transition-group";
import { Form, Input, Button, Checkbox } from 'antd';
const layout = {
  labelCol: {
    span: 2,
  },
  wrapperCol: {
    span: 8,
  },
};
const tailLayout = {
  wrapperCol: {
    offset: 2,
    span: 8,
  },
};

const Demo = () => {
  const onFinish = (values) => {
    console.log('Success:', values, this);
    this.props.getMsg(values)
  };

  const onFinishFailed = (errorInfo) => {
    console.log('Failed:', errorInfo);
  };

  return (
    <Form
      {...layout}
      name="basic"
      initialValues={{
        remember: true,
      }}
      onFinish={onFinish}
      onFinishFailed={onFinishFailed}
    >
      <Form.Item
        label="姓名"
        name="name"
        rules={[
          {
            required: true,
            message: '请输入用户名',
          },
        ]}
      >
        <Input />
      </Form.Item>

      <Form.Item
        label="年龄"
        name="age"
        rules={[
          {
            required: true,
            message: '请输入年龄',
          },
        ]}
      >
        <Input />
      </Form.Item>
      <Form.Item
        label="地址"
        name="address"
        rules={[
          {
            required: true,
            message: '请输入地址',
          },
        ]}
      >
        <Input />
      </Form.Item>
      <Form.Item {...tailLayout}>
        <Button type="primary" htmlType="submit">
          新增
        </Button>
      </Form.Item>
    </Form>
  );
};
class Practice extends Component {
  // 类名  继承  react组件
  constructor(props) {
    // 构造
    super(props); // 调用父级方法
    this.state = {
      name: "",
      age: "",
      address: "",
      list: [],
      articleListInfo: [],
    };
  }

  // componentWillMount () {
  //   console.log('componentWillMount-----组件将要挂载到页面的时候')
  // }

  // shouldComponentUpdate (nextProps, nextState) {
  //   console.log('shouldComponentUpdate-----组件更新之前  1')
  //   return true
  // }

  // componentWillUpdate () {
  //   console.log('componentWillUpdate-----组件更新之前  2')
  // }

  render() {
    return (
      <Fragment>
        {/* 外层类似vue必须加一层盒子，但是加了这个就可以不用增加额外盒子 */}
        {/* 绑定事件必须要.bind(this)进去，或者使用箭头函数，不然无法找到this */}
        <div>
          <Demo getMsg={this.handleGetMsg}/>
          {/* <label htmlFor="input">名字</label>
          <input
            id="input"
            value={this.state.name}
            onChange={(e) => {
              this.nameChange(e);
            }}
          />
          <label htmlFor="input">年龄</label>
          <input
            id="input"
            value={this.state.age}
            onChange={(e) => {
              this.ageChange(e);
            }}
          />
          <label htmlFor="input">地址</label>
          <input
            id="input"
            value={this.state.address}
            onChange={(e) => {
              this.addressChange(e);
            }}
          />
          <Button
            value="small"
            type="primary"
            onClick={() => {
              this.addClick();
            }}
          >
            增加
          </Button> */}
        </div>
        <ul
          ref={(ul) => {
            this.ulDom = ul;
          }}
        >
          <TransitionGroup>
            {this.state.list.map((item, index) => {
              return (
                <CSSTransition timeout={10} unmountOnExit appear={true} key={index}>
                  <PracticeItem
                    key={index}
                    content={item}
                    index={index}
                    delItem={() => {
                      this.delItem(index);
                    }}
                  />
                </CSSTransition>
              );
            })}
          </TransitionGroup>
        </ul>
      </Fragment>
    );
  }

  componentDidUpdate() {
    console.log("componentDidUpdate-----所有挂载完毕执行   4");
  }

  componentDidMount() {
    console.log("componentDidMount-----组件已经挂载到页面的时候");
  }
  handleGetMsg = (value) => {
    console.log(value)
    this.setState({
      list: value
    })
  }
  //新增
  addClick() {
    if (!this.state.name) return;
    this.setState(
      {
        // react单向流，需要绑定原始数据变化
        list: [{ name: this.state.name, age: this.state.age, address: this.state.address }],
        name: "",
        age: "",
        address: ""
      },
      () => {
        console.log(this.ulDom.querySelectorAll("li").length);
      }
    );
  }
  //删除
  delItem(index) {
    // 数据操作必须重新赋值操作，不能直接改变state里面的数据
    let list = this.state.list;
    list.splice(index, 1);
    this.setState({
      // react单向流，需要绑定原始数据变化
      list: list,
    });
  }
}

export default Practice;
