import "./index.css";
import React from "react";
import { articleList } from "../../lib/api";
import { Table, Input } from "antd";
const { Search } = Input;
const columns = [
  {
    title: "ID",
    dataIndex: "id",
    key: "id",
  },
  {
    title: "书名",
    dataIndex: "name",
    key: "name",
  },
  {
    title: "作者",
    dataIndex: "author",
    key: "author",
  },
  {
    title: "价格",
    dataIndex: "price",
    key: "price",
  },
];

const onSearch = (value) => {
  console.log(value);
};
class Index extends React.Component {
  // 类名  继承  react组件
  constructor(props) {
    // 构造
    super(props); // 调用父级方法
    this.state = {
      dataSource: [],
      pagination: {
        defaultPageSize: 5,

        showTotal: (total) => `共 ${total} 条数据`,

        showSizeChanger: true,

        pageSizeOptions: ["2", "10", "15", "20"],

        onShowSizeChange: (current, pageSize) => (this.pageSize = pageSize),
      },
    };
  }
  render() {
    return (
      <div>
        {/* 表头 */}
        <div className="search">
          <Search placeholder="input search text" allowClear enterButton="Search" size="large" onSearch={onSearch} />
        </div>

        <Table rowKey="id" bordered dataSource={this.state.dataSource} columns={columns} />
      </div>
    );
  }
  componentDidMount() {
    console.log("componentDidMount-----组件已经挂载到页面的时候");
    this.getBook(); //获取列表接口
  }
  async getBook() {
    let res = await articleList({ current: 1, size: 2 });
    console.log(33, res.content);
    if (res.content.length) {
      this.setState({
        dataSource: res.content,
      });
    }
  }
}

export default Index;
