import logo from '../../assets/images/logo.svg';
import './index.css';
import React from 'react';
import { articleList } from '../../lib/api'

class Index extends React.Component {  // 类名  继承  react组件
  constructor(props) {  // 构造
    super(props)  // 调用父级方法
    this.state = {
      inputValue: '',
      list: ['小米', '网大葱'],
      articleListInfo:[]
    }
  }
  render() {
    return (
      <div>


      </div>
    )
  }
  componentDidMount () {
    console.log('componentDidMount-----组件已经挂载到页面的时候')
    this.articleListA()
  }
  async articleListA () {
    let res = await articleList()
    if(res) {
      console.log(res)
      this.setState({
        articleListInfo: res.rows
      })
    }
  }
}

export default Index;
