import "./index.css";
import React from "react";
import { articleList } from "../../lib/api";
import { Table, Input } from "antd";
const { Search } = Input;
const columns = [
  {
    title: "ID",
    dataIndex: "id",
    key: "id",
  },
  {
    title: "书名",
    dataIndex: "name",
    key: "name",
  },
  {
    title: "作者",
    dataIndex: "author",
    key: "author",
  },
  {
    title: "价格",
    dataIndex: "price",
    key: "price",
  },
];

const onSearch = (value) => {
  console.log(value);
};
class Index extends React.Component {
  // 类名  继承  react组件
  constructor(props) {
    // 构造
    super(props); // 调用父级方法
    this.state = {
      dataSource: [],
      // 当前页
      current: 1,
      // 每页显示
      pageSize: 2,
      // 总条数
      total: 0,

      // 默认显示第几页

      defaultCurrent: 2,

      //是否点击跳转

      showQuickJumper: true,
    };
  }
  render() {
    return (
      <div>
        {/* 表头 */}
        <div className="search">
          <Search placeholder="input search text" allowClear enterButton="Search" size="large" onSearch={onSearch} />
        </div>

        <Table rowKey="id" bordered dataSource={this.state.dataSource} columns={columns} pagination={this.fenye()} />
      </div>
    );
  }
  componentDidMount() {
    console.log("componentDidMount-----组件已经挂载到页面的时候");
    this.getBook(); //获取列表接口
  }
  //分页

  fenye() {
    const fy = {
      // 当前页
      current: this.state.current,
      // 每页显示
      pageSize: this.state.pageSize,
      // 总条数
      total: this.state.total,
      // 点击下一页
      onChange: (current) => this.changPage(current),
      // 每页显示变化
      onShowSizeChange: (Current, pageSize) => {
        console.log(pageSize);
        this.onShowSizeChange(Current, pageSize);
      },
      // 默认第几页
      defaultCurrent: this.state.defaultCurrent,
      //是否可以跳转
      showQuickJumper: this.state.showQuickJumper,
    };

    return fy;
  }
  async getBook() {
    let res = await articleList({ current: 1, size: 2 });
    console.log(33, res.content);
    if (res.content.length) {
      this.setState({
        dataSource: res.content,
        total: res.totalElements
      });
    }
  }
}

export default Index;
