import React, { Component, Fragment } from "react";
import "./index.scss";
import PracticeItem from "./component/practiceItem";
import { Button } from "antd";
import { CSSTransition, TransitionGroup } from "react-transition-group";

class Xiaojiejie extends Component {
  // 类名  继承  react组件
  constructor(props) {
    // 构造
    super(props); // 调用父级方法
    this.state = {
      name: "",
      age: "",
      address: "",
      list: [],
      articleListInfo: [],
    };
  }

  // componentWillMount () {
  //   console.log('componentWillMount-----组件将要挂载到页面的时候')
  // }

  // shouldComponentUpdate (nextProps, nextState) {
  //   console.log('shouldComponentUpdate-----组件更新之前  1')
  //   return true
  // }

  // componentWillUpdate () {
  //   console.log('componentWillUpdate-----组件更新之前  2')
  // }

  render() {
    return (
      <Fragment>
        {/* 外层类似vue必须加一层盒子，但是加了这个就可以不用增加额外盒子 */}
        {/* 绑定事件必须要.bind(this)进去，或者使用箭头函数，不然无法找到this */}
        <div>
          <label htmlFor="input">名字</label>
          <input
            id="input"
            value={this.state.name}
            onChange={(e) => {
              this.nameChange(e);
            }}
          />
          <label htmlFor="input">年龄</label>
          <input
            id="input"
            value={this.state.age}
            onChange={(e) => {
              this.ageChange(e);
            }}
          />
          <label htmlFor="input">地址</label>
          <input
            id="input"
            value={this.state.address}
            onChange={(e) => {
              this.addressChange(e);
            }}
          />
          <Button
            value="small"
            type="primary"
            onClick={() => {
              this.addClick();
            }}
          >
            增加
          </Button>
        </div>
        <ul
          ref={(ul) => {
            this.ulDom = ul;
          }}
        >
          <TransitionGroup>
            {this.state.list.map((item, index) => {
              return (
                <CSSTransition timeout={10} unmountOnExit appear={true} key={index}>
                  <PracticeItem
                    key={index}
                    content={item}
                    index={index}
                    delItem={() => {
                      this.delItem(index);
                    }}
                  />
                </CSSTransition>
              );
            })}
          </TransitionGroup>
        </ul>
      </Fragment>
    );
  }

  componentDidUpdate() {
    console.log("componentDidUpdate-----所有挂载完毕执行   4");
  }

  componentDidMount() {
    console.log("componentDidMount-----组件已经挂载到页面的时候");
  }
  // 名字
  nameChange(e) {
    this.setState({
      // react单向流，需要绑定原始数据变化
      name: e.target.value,
    });
  }
  // 年龄
  ageChange(e) {
    this.setState({
      // react单向流，需要绑定原始数据变化
      name: e.target.value,
    });
  }
  // 地址
  addressChange(e) {
    this.setState({
      // react单向流，需要绑定原始数据变化
      name: e.target.value,
    });
  }
  //新增
  addClick() {
    if (!this.state.name) return;
    this.setState(
      {
        // react单向流，需要绑定原始数据变化
        list: [{ name: this.state.name, age: this.state.age, address: this.state.address }],
        name: "",
        age: "",
        address: ""
      },
      () => {
        console.log(this.ulDom.querySelectorAll("li").length);
      }
    );
    debugger;
    console.log("====================================");
    console.log(...this.state.list, this.state.name, [...this.state.list]);
    console.log("====================================");
  }
  //删除
  delItem(index) {
    // 数据操作必须重新赋值操作，不能直接改变state里面的数据
    let list = this.state.list;
    list.splice(index, 1);
    this.setState({
      // react单向流，需要绑定原始数据变化
      list: list,
    });
  }
}

export default Xiaojiejie;
