import "./index.css";
import React from "react";
import { articleList } from "../../lib/api";
import { Table, Input, Modal, Popconfirm, Row, Col } from "antd";
const { Search } = Input;

const onSearch = (value) => {
  console.log(value);
};
class Index extends React.Component {
  // 类名  继承  react组件
  constructor(props) {
    // 构造
    super(props); // 调用父级方法
    this.state = {
      dataSource: [],
      current: 1, // 当前页
      size: 5, // 每页显示
      total: 0, // 总条数
      showQuickJumper: true, //是否点击跳转
      isModalVisible: false,
      setIsModalVisible: false,
      columns: [
        {
          title: "ID",
          dataIndex: "id",
          key: "id",
        },
        {
          title: "书名",
          dataIndex: "name",
          key: "name",
        },
        {
          title: "作者",
          dataIndex: "author",
          key: "author",
        },
        {
          title: "价格",
          dataIndex: "price",
          key: "price",
        },
        {
          title: "操作",
          dataIndex: "operation",
          render: (_, record) => (
            <div>
              <span className="edit" title="Sure to delete?" onClick={() => this.handleEdit(record)}>
                <a>编辑</a>
              </span>
              <Popconfirm title="Sure to delete?" onConfirm={() => this.handleDelete(record.id)}>
                <a>删除</a>
              </Popconfirm>
            </div>
          ),
        },
      ],
      //编辑
      name: "",
    };
  }
  render() {
    return (
      <div>
        {/* 表头 */}
        <div className="search">
          <Search placeholder="input search text" allowClear enterButton="Search" size="large" onSearch={onSearch} />
        </div>
        <Table rowKey="id" bordered dataSource={this.state.dataSource} columns={this.state.columns} pagination={this.fenye()} />

        <Modal okText="确认" cancelText="取消" title="编辑" visible={this.state.isModalVisible} onOk={() => this.handleOk()} onCancel={() => this.handleCancel()}>
          <Row>
            <Col span={2}>书名</Col>
            <Col span={16}>
              <Input placeholder="书名" value={this.state.name} onChange={()=>this.nameChange(event)} />
            </Col>
          </Row>
          <Row>
            <Col span={2}>作者</Col>
            <Col span={16}>
              <Input placeholder="作者" value={this.state.name} onChange={()=>this.nameChange(event)} />
            </Col>
          </Row>
          <Row>
            <Col span={2}>书名</Col>
            <Col span={16}>
              <Input placeholder="书名" value={this.state.name} onChange={()=>this.nameChange(event)} />
            </Col>
          </Row>
          <Row>
            <Col span={2}>书名</Col>
            <Col span={16}>
              <Input placeholder="书名" value={this.state.name} onChange={()=>this.nameChange(event)} />
            </Col>
          </Row>
        </Modal>
      </div>
    );
  }
  componentDidMount() {
    console.log("componentDidMount-----组件已经挂载到页面的时候");
    this.getBook(this.state.current, this.state.size); //获取列表接口
  }
  //分页
  fenye() {
    const fy = {
      current: this.state.current, // 当前页
      pageSize: this.state.size, // 每页显示
      total: this.state.total, // 总条数
      onChange: (current) => {
        // 点击下一页
        this.getBook(current, 5);
        this.setState({
          current: current,
        });
      },
      // 每页显示变化
      onShowSizeChange: (Current, pageSize) => {
        console.log(pageSize);
        this.getBook(Current, pageSize);
      },
      showQuickJumper: this.state.showQuickJumper, //是否可以跳转
    };
    return fy;
  }
  async getBook(current, size) {
    let res = await articleList({ current: current, size: size });
    console.log(33, res.content);
    if (res.content.length) {
      this.setState({
        dataSource: res.content,
        total: res.totalElements,
      });
    }
  }
  //删除
  handleDelete(key) {
    console.log("====================================");
    console.log(key);
    console.log("====================================");
    // const dataSource = [...this.state.dataSource];
    // this.setState({
    //   dataSource: dataSource.filter((item) => item.key !== key),
    // });
  }
  //编辑
  handleEdit(key) {
    let name = key.name;
    this.setState({
      isModalVisible: true,
      name: name,
    });
    console.log("====================================");
    console.log(key);
    console.log("====================================");
    // const dataSource = [...this.state.dataSource];
    // this.setState({
    //   dataSource: dataSource.filter((item) => item.key !== key),
    // });
  }
  nameChange(event) {
    this.setState({ name: event.target.value });
  }
  handleOk() {
    console.log(this);
    this.setState({
      isModalVisible: false,
    });
  }

  handleCancel() {
    this.setState({
      isModalVisible: false,
    });
  }
}

export default Index;
