import React, { Component } from 'react';
import { Link, Redirect } from 'react-router-dom'
import store from '../../store/index'
import { connect } from 'react-redux'
import { Input, Button, List } from 'antd'
import { inputValueChangeA, addBtnA, delBtnA, getListA } from '../../store/actions'
import * as Types from '../../store/actionTypes'
import { articleList } from '../../lib/api'
import './index.scss';

class TodoList extends Component {
  render() {
    let { inputValue, inputValueChange, addBtn, rows, delBtn } = this.props
    return (
      <div className="todoList">
        {/* 重定向 */}
        {/* <Redirect to="/" /> */} 
        <div>
          <Input 
            placeholder={inputValue}
            style={{ width: '250px' }}
            onChange={(e)=>{inputValueChange(e)}}
            value={inputValue}
          ></Input>
          <Button type="primary" onClick={()=>{addBtn()}}>增加</Button>
        </div>
        <div className="todoList-list">
          <List
            dataSource={rows}
            renderItem={(item, index) => (
              <List.Item>
                <Link to={{pathname: '/todoListDetail', params:item}}>{index+1}、{item.title}</Link>
                <Button type="danger" onClick={()=>{delBtn(index)}}>删除</Button>
              </List.Item>
            )}
          />
        </div>
      </div>
    );
  }

  componentDidMount () {
    this.getList()
  }

  async getList () {
    let res = await articleList({
      pagingDto: {
        pageNo: 1,
        pageSize: 5
      }
    })
    if(res) {
      const action = getListA({
        rows: res.rows
      })
      store.dispatch(action)
    }
  }
}

// 属性交互store
const stateToProps = (state) => {
  return {
    inputValue: state.inputValue,
    rows: state.rows,
  }
}

// 方法交互store
const dispatchToProps = (dispatch) => {
  return {
    inputValueChange (e) {
      const action = inputValueChangeA({
        value: e.target.value
      })
      store.dispatch(action)
    },
    addBtn () {
      // const action = addBtnA()
      // store.dispatch(action)
    },
    delBtn (index) {
      const action = delBtnA({
        value: index
      })
      store.dispatch(action)
    }
  }
}

export default connect(stateToProps, dispatchToProps)(TodoList);
