import React, { Component, Fragment } from "react";
import "./index.scss";
import PracticeItem from "./component/practiceItem";

import { CSSTransition, TransitionGroup } from "react-transition-group";
import { Form, Input, Button, Checkbox } from 'antd';
const layout = {
  labelCol: {
    span: 2,
  },
  wrapperCol: {
    span: 8,
  },
};
const tailLayout = {
  wrapperCol: {
    offset: 2,
    span: 8,
  },
};

class Practice extends Component {
  // 类名  继承  react组件
  constructor(props) {
    // 构造
    super(props); // 调用父级方法
    this.state = {
      name: "",
      age: "",
      address: "",
      list: [],
    };
  }

  // componentWillMount () {
  //   console.log('componentWillMount-----组件将要挂载到页面的时候')
  // }

  // shouldComponentUpdate (nextProps, nextState) {
  //   console.log('shouldComponentUpdate-----组件更新之前  1')
  //   return true
  // }

  // componentWillUpdate () {
  //   console.log('componentWillUpdate-----组件更新之前  2')
  // }

  render() {
    return (
      <Fragment>
        {/* 外层类似vue必须加一层盒子，但是加了这个就可以不用增加额外盒子 */}
        {/* 绑定事件必须要.bind(this)进去，或者使用箭头函数，不然无法找到this */}
        <div>
          <Form
            {...layout}
            name="basic"
            initialValues={{
              remember: true,
            }}

            ref="form"
            onFinish={(values) => this.onAdd(values)}
            onFinishFailed={this.onFinishFailed}
          >
            <Form.Item
              label="姓名"
              name="name"
              rules={[
                {
                  required: true,
                  message: '请输入用户名',
                },
              ]}
            >
              <Input />
            </Form.Item>

            <Form.Item
              label="年龄"
              name="age"
              rules={[
                {
                  required: true,
                  message: '请输入年龄',
                },
              ]}
            >
              <Input />
            </Form.Item>
            <Form.Item
              label="地址"
              name="address"
              rules={[
                {
                  required: true,
                  message: '请输入地址',
                },
              ]}
            >
              <Input />
            </Form.Item>
            <Form.Item {...tailLayout}>
              <Button type="primary" htmlType="submit">
                新增
              </Button>
            </Form.Item>
          </Form>
        </div>
        <ul

        >
          <TransitionGroup>
            {this.state.list.map((item, index) => {
              return (
                <CSSTransition timeout={10} unmountOnExit appear={true} key={index}>
                  <PracticeItem
                    key={index}
                    content={item}
                    index={index}
                    delItem={() => {
                      this.delItem(index);
                    }}
                  />
                </CSSTransition>
              );
            })}
          </TransitionGroup>
        </ul>
      </Fragment>
    );
  }

  componentDidUpdate() {
    console.log("componentDidUpdate-----所有挂载完毕执行   4");
  }

  componentDidMount() {
    console.log("componentDidMount-----组件已经挂载到页面的时候");
  }
  //新增
  onAdd(values) {
    this.state.list.push(values);
    this.setState(
      {
        list: this.state.list
      },
    );
    if (values) {
      this.refs.form.resetFields();
    }
  };
  onFinishFailed(errorInfo) {
    console.log('Failed:', errorInfo);
  };
  //删除
  delItem(index) {
    // 数据操作必须重新赋值操作，不能直接改变state里面的数据
    let list = this.state.list;
    list.splice(index, 1);
    this.setState({
      list: list,
    });
  }
}

export default Practice;
