import logo from '../../assets/images/logo.svg';
import './index.css';
import React from 'react';
import { articleList } from '../../lib/api'
import { Table, Tag, Space } from 'antd';
const columns =  [
  {
    title: '姓名',
    dataIndex: 'id',
    key: 'id',
  },
  {
    title: '书名',
    dataIndex: 'name',
    key: 'name',
  },
  {
    title: '作者',
    dataIndex: 'author',
    key: 'author',
  },
];

class Index extends React.Component {  // 类名  继承  react组件
  constructor(props) {  // 构造
    super(props)  // 调用父级方法
    this.state = {
      inputValue: '',
      list: ['小米', '网大葱'],
      articleListInfo:[],
      dataSource:[]
    }
  }
  render() {
    return (
      <div>

       <Table dataSource={this.state.dataSource} columns={columns} />
      </div>
    )
  }
  componentDidMount () {
    console.log('componentDidMount-----组件已经挂载到页面的时候')
    this.articleListA()
  }
  async articleListA () {
    let res = await articleList()
    if(res) {
      console.log(res)
      this.setState({
        dataSource: res
      })
    }
  }
}

export default Index;
