import "./index.css";
import React from "react";
import { articleDetail } from "../../lib/api";

class Index extends React.Component {
  // 类名  继承  react组件
  constructor(props) {
    super(props);
    this.state = {
      id: undefined,
      bookDetail: {},
    };
  }
  render() {
    return (
      <div>
        <p>书名： {this.state.name}</p>
      </div>
    );
  }
  componentWillMount() {}
  componentDidMount() {
    console.log("componentDidMount-----组件已经挂载到页面的时候");
    // console.log(11, this.props.match.params.id);
    console.log(22, this.props.location.state.id);
    let id = this.props.location.state.id;
    this.getBookDetail(id);
  }
  getBookDetail(id) {
    articleDetail(id).then((res) => {
      console.log("====================================");
      console.log(res);
      console.log("====================================");
      let data = res.data.data;
      if (data) {
        this.setState({
          bookDetail: data,
        });
      }
    });
  }
}

export default Index;
